changeSet.createProcedure:
 create trigger end_point_contact_bi
    before insert on END_POINT_CONTACT_T
    for each row
begin
  if :new.END_POINT_CONTACT_ID is null
  then
    select sq_end_point_contact.nextval into :new.END_POINT_CONTACT_ID from dual;
  end if;
end;

  	CREATE OR REPLACE FUNCTION end_point_contact_bi()
		  RETURNS trigger AS
		 $BODY$
		begin

		  if NEW.end_point_contact_id IS NULL 
			then
				begin
					NEW.end_point_contact_id :=nextval('sq_end_point_contact');
				end;  
			end if;
		  RETURN NEW;	
		  EXCEPTION
		     WHEN OTHERS THEN 
		     RAISE WARNING 'end_point_contact_bi: SQLSTATE %: %', SQLSTATE, SQLERRM;
		     RETURN NULL;  	
		end;$BODY$
		  LANGUAGE plpgsql VOLATILE
		  COST 100;

		CREATE TRIGGER end_point_contact_bi BEFORE INSERT ON end_point_contact_t
		    FOR EACH ROW EXECUTE PROCEDURE end_point_contact_bi();

  	create  trigger channel_BI
    before insert on channel_T
    for each row
		begin
		  if :new.channel_ID is null
		  then
		    select SQ_channel.nextval into :new.channel_ID from dual;
		  end if;

		  /* Fill date and user created */
		  IF :NEW.created_on IS NULL
		  THEN
		      :NEW.created_on := SYSDATE;
		  END IF;

		  IF :NEW.created_by IS NULL
		  THEN
		      :NEW.created_by := UPPER(sys_context('USERENV', 'OS_USER'));
		  ELSE
		      :NEW.created_by := UPPER(:NEW.created_by);
		  END IF;
		end;

	  	create or replace trigger channel_BU
    	before update on channel_T
    	for each row
		begin
		    /* Fill date and user modified */
		    IF :NEW.modified_on IS NULL OR (:NEW.modified_on = :OLD.modified_on)
		    THEN
		        :NEW.modified_on := SYSDATE;
		    END IF;

		    IF :NEW.modified_by IS NULL OR (:NEW.modified_by = :OLD.modified_by)
		    THEN
		        :NEW.modified_by := UPPER(sys_context('USERENV', 'OS_USER'));
		    ELSE
		        :NEW.modified_by := UPPER(:NEW.modified_by);
		    END IF;

		end;

  	CREATE OR REPLACE FUNCTION channel_BI()
		  RETURNS trigger AS
		 $BODY$
		begin

		  if NEW.channel_id IS NULL 
			then
				begin
					NEW.channel_id :=nextval('SQ_CHANNEL');
				end;  
			end if;


		  IF NEW.created_on IS NULL
		  THEN
		      NEW.created_on := current_timestamp;
		  END IF;	

		  if NEW.created_by is NULL
		  THEN 
		    NEW.created_by :=UPPER(current_user);
		  ELSE
		  	NEW.created_by :=UPPER(NEW.created_by);  
		  END IF;  
		  RETURN NEW;	
		  EXCEPTION
		     WHEN OTHERS THEN 
		     RAISE WARNING 'channel_BI: SQLSTATE %: %', SQLSTATE, SQLERRM;
		     RETURN NULL;  	
		end;$BODY$
		  LANGUAGE plpgsql VOLATILE
		  COST 100;

		CREATE TRIGGER channel_BI BEFORE INSERT ON channel_T
		    FOR EACH ROW EXECUTE PROCEDURE channel_BI();

  	CREATE OR REPLACE FUNCTION channel_BU()
  RETURNS trigger AS
 $BODY$
begin

  IF NEW.modified_on IS NULL or (OLD.modified_on = OLD.modified_on)
  THEN
      NEW.modified_on := current_timestamp;
  END IF;	

  if NEW.modified_by is NULL or (NEW.modified_by = OLD.modified_by)
  THEN 
    NEW.modified_by :=UPPER(current_user);
  ELSE
  	NEW.modified_by :=UPPER(NEW.modified_by);  
  END IF;  
  RETURN NEW;	
  EXCEPTION
     WHEN OTHERS THEN 
     RAISE WARNING 'channel_BU: SQLSTATE %: %', SQLSTATE, SQLERRM;
     RETURN NULL;  	
end;$BODY$
  LANGUAGE plpgsql VOLATILE
  COST 100;

CREATE TRIGGER channel_BU BEFORE UPDATE ON channel_T
    FOR EACH ROW EXECUTE PROCEDURE channel_BU();

